mapper.select:
		/* LocaleMapper.selectLocale */
		SELECT im.code
				, im.lang
				, IFNULL(im.desc, '') AS desc
				, im.msg
		  FROM i18n_msg im
 		  INNER JOIN i18n_lang il
		     ON im.lang = il.lang
		 WHERE im.code = #{code}
		   AND im.lang = #{lang}

		/* LocaleMapper.selectLocaleMessage */
		SELECT im.msg
		  FROM i18n_msg im
 		  INNER JOIN i18n_lang il
		     ON im.lang = il.lang
		 WHERE im.code = #{code}
		   AND im.lang = #{lang}
